package javabook2.ch03;

public class BitShift {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int result =  1 << 3;
		System.out.println(result);
		
		int result2 = -8 >> 3;
		System.out.println(result2);
		
		int result3 = -8 >>> 3;
		System.out.println(result3);
	}

}
